INSERT INTO public."Book" (id, title, author, description, price, stock, "createdAt", "updatedAt", images) VALUES ('3c6680f2-781c-446f-9092-69d2c24d7608', 'Ramadan Activity Book (Little Kids)', 'Learning Roots', 'Would you like your child to eagerly await the coming of Ramadan? Do you feel frustrated because each year, Ramadan passes by without your child truly appreciating the blessings of the month? Do you wish your children knew more about Ramadan and cherished its inner and outer beauty? We know how you feel because we’ve been there too. What we really need is a way to both excite and educate our children about Ramadan in a way that endears their hearts to the great month and shows its blessings as a golden opportunity for them to get closer to Allah. That’s exactly why we designed the Learning Roots Ramadan Activity Book. Features: 34 laminated pocket-sized cards Recommended age 5-7 years old', 18, 3, '2025-06-12 01:54:12.731', '2025-06-13 16:01:48.665', '{https://cdn.shopify.com/s/files/1/0732/0879/1270/files/ramadan-little-hero-shot.webp?v=1741712715,https://cdn.shopify.com/s/files/1/0732/0879/1270/files/B42A3768-Edit.webp?v=1741712715,https://cdn.shopify.com/s/files/1/0732/0879/1270/files/B42A3746-Edit.webp?v=1741712715}');
