INSERT INTO public."Book" (id, title, author, description, price, stock, "createdAt", "updatedAt", images) VALUES ('e8a35b08-b72f-4322-9f43-660ae47aeaed', 'Box of Manners', 'Learning Roots', 'Each dua is isolated on its own card in large text and broken down into small chunks making it easy for your child to memorise. Every dua is translated in English so your child can unlock the powerful meanings of these words. There are cute pictures illustrating each dua in action so that your child can visualise when and where the words wold be used. The Kiitab (sold separately) functions read and translate each dua, as well as explain the virtue of each dua to your child. Features: 27 laminated cards Recommended age 5+ Each card measures 100mm x 160mm Compatible with Kiitab (sold separately)', 22, 1, '2025-06-12 01:54:12.824', '2025-06-13 16:01:48.875', '{https://cdn.shopify.com/s/files/1/0732/0879/1270/files/Box1.webp?v=1741380698,https://cdn.shopify.com/s/files/1/0732/0879/1270/files/render11.webp?v=1741380698,https://cdn.shopify.com/s/files/1/0732/0879/1270/files/book-render3.webp?v=1741380698}');
